boolean get(unsigned int o) FASTCALL\
{
    odummy=search_object_p(o);
    if(odummy->position!=current_position) {
        show_message(message1006);
    } else if((odummy->attributes&ISNOTMOVABLE)==0) {
        show_message(message1005);
    } else if(counter[120]+odummy->weight>counter[122]){ 
        show_message(message1003);
    } else if(counter[124]+odummy->size>counter[121]) {
        show_message(message1004);
    } else {
        odummy->position=CARRIED;
        ++counter[119];
        counter[120]+=odummy->weight;
        counter[124]+=odummy->size;
        return false;
    }
    return true;
}
